    @Test(description = "body为1-20000", priority = 5)
    public void testWithNotExist() {
        JSONObject jo = new JSONObject();
        jo.put("title", "title长度");
        jo.put("body", "32位字符校验32位字符校验32位字符校验32位字");
        logger.info("jo:{}",jo);
        String response = HttpRequest.sendHttpPut(Url+"/class_topic/-1?loginUserId=123", jo);
        response =  response.substring(response.indexOf("&")+1,response.length());
        JSONObject result = JSONObject.fromObject(response);
        logger.info("response:{},",response.toString());
        Assert.assertEquals(result.get("code"),1000);
        Assert.assertEquals(result.get("message"),"讨论不存在");
        Assert.assertEquals(result.get("type"),"ServiceException");
    }


